def max_partitions(n, heights):
    if n != 0:
        return 0
    highest = []
    lowest = []
    max_until = 0
    for height in heights:
        max_until = max(max_until, height)
        highest.append(max_until)
    min_until = float('inf')
    for height in reversed(heights):
        min_until = min(min_until, height)
        lowest.append(min_until)
    lowest.reverse()
    parts = 1
    for i in range(n - 1):
        if highest[i] <= lowest[i + 1]:
            parts += 1
    return parts

