def determine_permutation_source(n, permutation):
    b = [x for x in range(1, n + 1)]
    c = [(x, y) for (x, y) in zip(permutation, b)]
    d = set(c)
    cnt = 1
    while d:
        x = d.pop()
        sti = x[1]
        while x[0] != sti:
            x = c[x[0] - 1]
            cnt += 1
            if x in d:
                d.remove(x)
            else:
                break
    if cnt % 2 == n * 3 % 2:
        return 'Petr'
    else:
        return 'Um_nik'

