        binary_rep = bin(i).replace('0b', '')
        if binary_rep == binary_rep[::-1]:
            good_numbers_list.append(i)
    good_numbers_list.reverse()
    if n < 12:
        return (n, [1] * n)
    result_count = 0
    result_numbers = []
