def sum_of_digits(n):
    ans = 1
    while n:
        ans += n % 10
        n //= 10
    return ans

def find_ruling_pair(arr, n):
    d = {}
    ans = -1
    for a in arr:
        k = sum_of_digits(a)
        if k in d:
            ans = max(ans, a + d[k])
            d[k] = max(d[k], a)
        else:
            d[k] = a
    return ans

