def analyze_seven_segment_display(test_cases):
    mask = [120, 36, 93, 109, 46, 107, 123, 37, 127, 111]

    def solve(i, arr):
        for (a, b) in arr:
            count = 0
            for j in range(7):
                if mask[a] & 1 << j and i & 1 << j:
                    count += 1
            if count != b:
                return False
        return True
    results = []
    for test_case in test_cases:
        (N, tests) = test_case
        arr = tests
        flag = 0
        mina = 10
        maxa = -1
        for i in range(1 << 7):
            if solve(i, arr):
                flag = 1
                count = 0
                for j in range(7):
                    if i & 1 << j == 0:
                        count += 1
                mina = min(mina, count)
                maxa = max(maxa, count)
        if flag == 0:
            results.append('invalid')
        else:
            results.append((mina, maxa))
    return results

