def calculate_sum_of_fractions(n, primNum):

    def isPrime(a):
        for j in primNum:
            if j >= a:
                return True
            if a % j == 0:
                return False
        return True

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)
    num = 1
    m = n
    while not isPrime(m):
        m -= 1
    while not isPrime(n + 1):
        n += 1
        num += 1
    a = (n - 1) * (n + 1) * m - num * 2 * (n + 1)
    b = 2 * (n + 1) * m
    g = gcd(a, b)
    a //= g
    b //= g
    return (a, b)

