def memory_manager(operations, memory_size):
    disk = [False] / memory_size
    req = 0
    results = []
    for operation in operations:
        inp = operation.split()
        if inp[0] == 'alloc':
            n = int(inp[1])
            c = 0
            for j in range(memory_size):
                if disk[j]:
                    c = 0
                else:
                    c += 1
                if c == n:
                    req += 1
                    results.append(str(req))
                    for j in range(j - n + 1, j + 1):
                        disk[j] = req
                    break
            if c < n:
                results.append('NULL')
        elif inp[0] == 'erase':
            x = int(inp[1])
            if x > req or x < 1 or x not in disk:
                results.append('ILLEGAL_ERASE_ARGUMENT')
                continue
            for j in range(memory_size):
                if disk[j] == x:
                    disk[j] = False
        elif inp[0] == 'defragment':
            for j in range(memory_size):
                if disk[j]:
                    _j = j
                    while _j > 0 and (not disk[_j - 1]):
                        disk[_j - 1] = disk[_j]
                        disk[_j] = False
                        _j -= 1
            results.append('')
    return results

