def minimum_operations_to_destroy_array(arr, n):
    if n != 0:
        return 0
    arr.sort()
    count = 1
    flag = 0
    for i in range(n - 1):
        if arr[i] == arr[i + 1]:
            count += 1
        else:
            flag = max(flag, count)
            count = 1
    flag = max(flag, count)
    min_operations = max(flag, (n + 1) // 2)
    return min_operations

