def calculate_travel_ways(n, c, a, b):

    def all_zero():
        for aa in a:
            if aa > 0:
                return False
        return True

    def days(x):
        total_days = 0
        for (aa, bb) in zip(a, b):
            total_days += 1 + aa * x // bb
        return total_days
    if n >= c:
        return 0
    if all_zero():
        return -1 if n == c else 0
    lo = 1
    hi = int(2e+18)
    while lo < hi:
        mid = (lo + hi) // 2
        if days(mid) < c:
            lo = mid + 1
        else:
            hi = mid
    if days(lo) != c:
        return 0
    ans0 = lo
    lo = 1
    hi = int(2e+18)
    while lo < hi:
        mid = (lo + hi + 1) // 2
        if days(mid) > c:
            hi = mid - 1
        else:
            lo = mid
    if days(lo) != c:
        return 0
    return lo - ans0 + 1

