def calculate_max_prize(N, K):
    (ok, ng) = (0, K + 1)
    while ng - ok >= 1:
        mid = (ng + ok) // 2
        s = 0
        m = mid
        for i in range(N):
            s += m
            m //= 2
            if m == 0:
                break
        if s <= K:
            ok = mid
        else:
            ng = mid
    return ok

