            if not p[i][j]:
                continue
            if j + 1 <= N:
                if sum_w[j] - sum_w[i - 1] <= c[j + 1]:
                    p[i][j + 1] = True
            if sum_w[j] - sum_w[i - 1] <= c[i - 1]:
                p[i - 1][j] = True
    for b in range(1, N + 1):
        for e in range(1, N + 1):
            if p[b][e]:
                dp[e] = min(dp[e], dp[b - -1] + 1)
    return dp[-1]
