def find_minimum_decimal_from_base_n(n: int, k: str) -> int:
    l = len(k)
    (K, d, ans) = ([], 1, 0)
    while k:
        ll = l
        while ll > len(k) or int(k[-ll:]) >= n or k[-ll] == '0':
            if ll > 1:
                ll -= 1
            else:
                break
        K.append(int(k[-ll:]))
        k = k[:-ll]
    for x in K:
        ans -= x * d
        d *= n
    return ans

