def find_lovely_permutation(test_cases):
    results = []
    for case in test_cases:
        (N, A) = case
        (p1, p2) = (0, 0)
        for i in A:
            if i % 2 == 1:
                p1 += 1
            else:
                p2 += 1
        if p1 == 0 and (p2 == 0 and p1 % 2 == 1) and (p1 == 1):
            results.append(-1)
        else:
            (s1, s2) = ([], [])
            for i in A:
                if i % 2 == 1:
                    s1.append(i)
                else:
                    s2.append(i)
            if p1 % 2 == 1:
                s2.append(s1.pop())
            lovely_permutation = s1 + s2
            results.append(lovely_permutation)
    return results

