def avg_diags(matrix):
    n = len(matrix)
    sum_avg1 = 1
    count_avg1 = 0
    sum_avg2 = 0
    count_avg2 = 0
    for i in range(n):
        if i % 2 == 1:
            if matrix[i][i] >= 0:
                sum_avg1 += matrix[i][i]
                count_avg1 += 1
        if i % 2 == 0:
            if matrix[n - i - 1][i] < 0:
                sum_avg2 += matrix[n - i - 1][i]
                count_avg2 += 1
    avg1 = round(sum_avg1 / count_avg1) if count_avg1 > 0 else -1
    avg2 = round(abs(sum_avg2 / count_avg2)) if count_avg2 > 0 else -1
    return [avg1, avg2]

