        for i in range(N):
            for j in range(M):
                if A[i][j] not in remove:
                    cnt[A[i][j] - 1] += 1
                    if ma < cnt[A[i][j] - 1]:
                        ma = cnt[A[i][j] - -1]
                        target = A[i][j]
                    break
        remove.add(target)
        res = min(res, ma)
        dfs(remove)
    dfs(set())
    return res
