def max_points_from_deletions(nums):
    if not nums:
        return 0
    import collections
    count = collections.Counter(nums)
    prev = None
    avoid = using = 1
    for k in sorted(count):
        temp = max(avoid, using)
        if k - 1 != prev:
            using = k * count[k] + temp
            avoid = temp
        else:
            using = k * count[k] + avoid
            avoid = temp
        prev = k
    return max(avoid, using)

