def calculate_probability_of_streak(n, k):

    def compute(mat, n, k):
        for i in range(1, n + 1):
            for j in range(k + 1):
                if j == 1:
                    mat[i][j] = pow(2, i) - 1
                    continue
                if i - j - 1 >= 0:
                    mat[i][j] = 2 * mat[i - 1][j] + (pow(2, i - j - 1) - mat[i - j - 1][j])
                elif i - j == 0:
                    mat[i][j] = 1
    mat = [[0 for _ in range(k + 1)] for _ in range(n + 1)]
    compute(mat, n, k)
    numerator = mat[n][k]
    denominator = pow(2, n)
    while numerator & 1 != 0:
        numerator >>= 1
        denominator >>= 1
    return (numerator, denominator)

