    for test_case in test_cases:
        n = len(test_case)
        mat = test_case
        sets = []
        for j in range(5):
            temp = set()
            for i in range(n):
                if mat[i][j] == 1:
                    temp.add(i)
            if len(temp) >= n / 3:
                sets.append(temp)
        if len(sets) < 2:
            results.append('NO')
            continue
        combs = combinations(sets, 2)
        found = False
        for comb in combs:
            if len(comb[0] | comb[1]) == n:
                results.append('YES')
                found = True
                break
        if not found:
            results.append('NO')
    return results

