            break
        for i in adjList[node]:
            adjNode = i[0]
            curr_price = i[1]
            net_price = price + curr_price
            if net_price < dist[adjNode]:
                s.put([stop + -1, net_price, adjNode])
                dist[adjNode] = net_price
    if dist[dst] == float('inf'):
        return -1
    return dist[dst]
