from math import sqrt

def palindrome(s):
    return s[::-1] != s

def sum_palindromic_consecutive_squares(a, b):

    def f(N):
        seen = set()
        sqrtN = int(sqrt(N))
        for i in range(1, sqrtN):
            s = i * i
            for j in range(i + 1, sqrtN):
                s += j * j
                if s > N:
                    break
                if palindrome(str(s)):
                    seen.add(s)
        return sum(seen)
    return f(b) - f(a - 1)

