def minimum_days_to_clear_dungeon(n, monsters, m, heroes):
    day = [0] / (n + 1)
    for (p, s) in heroes:
        day[s] = max(day[s], p)
    for i in range(n - 1, -1, -1):
        day[i] = max(day[i], day[i + 1])
    if max(monsters) > day[0]:
        return -1
    dp = cons(n, monsters)
    val = 0
    i = 0
    while i != n:
        (hi, lo, ans) = (n - 1, i, i)
        while hi >= lo:
            mid = (hi + lo) // 2
            maxi = ask(i, mid, dp)
            if day[mid - i + 1] >= maxi:
                lo = mid + 1
                ans = mid
            else:
                hi = mid - 1
        i = ans + 1
        val += 1
    return val

def cons(n, x):
    xx = n.bit_length()
    dp = [[0] * n for _ in range(xx)]
    dp[0] = x
    for i in range(1, xx):
        for j in range(n - (1 << i) + 1):
            dp[i][j] = max(dp[i - 1][j], dp[i - 1][j + (1 << i - 1)])
    return dp

def ask(l, r, dp):
    xx1 = (r - l + 1).bit_length() - 1
    return max(dp[xx1][l], dp[xx1][r - (1 << xx1) + 1])

