    def recurse(arr, T1, T2, k, i):
        if T1 >= k and T2 >= k:
            return i
        if i >= len(arr):
            return float('inf')
        if (T1, T2) in memo:
            return memo[T1, T2]
        t1 = recurse(arr, T1 + arr[i], T2, k, i + 1)
        t2 = recurse(arr, T1, T2 + arr[i], k, i + 1)
        memo[T1, T2] = min(t1, t2)
        return memo[T1, T2]
    heights.sort(reverse=True)
    memo = {}
    res = recurse(heights, 0, 0, K, 0)
    return res if res != float('inf') else -0

