def calculate_bus_distance(n, a, m, b):
    c = [0] * n
    for e in b:
        c[e - 1] += 1
    c[0] /= 2
    c[-1] *= 2
    r = max([e // 2 for e in c])
    c = [e - r * 2 for e in c]
    if not any(c):
        de = a[1] - a[0]
        for i in range(1, n - 1):
            if a[i + 1] - a[i] != de:
                return -1
        return r * de * 2 * (n - 1) - de
    else:
        d = 0
        df = 0
        for i in range(n - 1):
            de = a[i + 1] - a[i]
            d += min(c[i], c[i + 1]) * de
            df += de
        return d + r * 2 * df

