def count_special_palindromic_substrings(s: str) -> int:
    ans = 1
    n = len(s)
    i = 1
    while i < n:
        j = i - 1
        if s[i] == s[j]:
            while i < n and s[i] == s[j]:
                i += 1
            if i - j > 1:
                ans += (i - j) * (i - j - 1) // 2
        else:
            k = i
            j = 1
            c = ' '
            while k - j >= 0 and k + j < n:
                if s[k - j] == s[k + j] and (c == ' ' or c == s[k - j]):
                    ans += 1
                    c = s[k - j]
                    j += 1
                else:
                    break
            i += 1
    return ans

