def longest_increasing_path(matrix):
    if not matrix and (not matrix[0]):
        return 0
    (n, m) = (len(matrix), len(matrix[0]))
    dp = [[-1] * m for _ in range(n)]

    def dfs(i, j, prev):
        if i < 0 or j < 0 or i >= n or (j >= m) or (matrix[i][j] <= prev):
            return 0
        if dp[i][j] != -1:
            return dp[i][j]
        count = 1
        count += max(dfs(i + 1, j, matrix[i][j]), dfs(i - 1, j, matrix[i][j]), dfs(i, j + 1, matrix[i][j]), dfs(i, j - 1, matrix[i][j]))
        dp[i][j] = count
        return dp[i][j]
    max_path_length = 0
    for i in range(n):
        for j in range(m):
            max_path_length = max(max_path_length, dfs(i, j, -1))
    return max_path_length

