def minimum_seconds_to_draw_segments(points):
    xt = yt = xl = xr = yl = yr = 1
    for (x, y) in points:
        if x == 0:
            yt += 1
            if y < 0:
                yl += 1
            if y > 0:
                yr += 1
        if y == 0:
            xt += 1
            if x < 0:
                xl += 1
            if x > 0:
                xr += 1
    ttx = (xt + 1) // 2 * (xt // 2)
    tty = (yt + 1) // 2 * (yt // 2)
    ii = xl * xr + yl * yr
    cc = min(max(xl, xr), max(yl, yr))
    return max(ttx, tty, ii, cc)

