def maximize_happiness(N, P, Q, cafeteria_happiness):
    total_cafeteria_happiness = sum(cafeteria_happiness)
    ans = [total_cafeteria_happiness]
    difs = [P * (Q - i) + cafeteria_happiness[i] for i in range(N)]
