            if i == 0:
                vertical.append([new_flag[i], 1])
            elif new_flag[i] == vertical[-1][0]:
                vertical[-1][1] += 1
            else:
                vertical.append([new_flag[i], 1])
        else:
            f2 = False
            break
    if not check_stripes(vertical):
        f2 = False
    if f1 or f2:
        return 'YES'
    else:
        return 'NO'

