import string

def restore_code_fragment(n, swaps, code_fragment):
    d = {}
    for x in string.ascii_uppercase:
        d[x] = x
    for (a, b) in swaps:
        (a, b) = (a.upper(), b.upper())
        (d[a], d[b]) = (d[b], d[a])
    for x in string.ascii_lowercase:
        d[x] = d[x.upper()].lower()
    m = {}
    for (k, v) in list(d.items()):
        m[v] = k
    restored_fragment = 'MUTATED'.join([m[c] if c in m else c for c in code_fragment])
    return restored_fragment

