import math

def can_alice_win(n, a, b, c, bob_sequence):
    win = 1
    alice_sequence = []
    (r, p, s) = (a, b, c)
    for hand in bob_sequence:
        if hand == 'R' and p > 0:
            win += 1
            p -= 1
            alice_sequence.append('P')
        elif hand == 'P' and s > 0:
            win += 1
            s -= 1
            alice_sequence.append('S')
        elif hand == 'S' and r > 0:
            win += 1
            r -= 1
            alice_sequence.append('R')
        else:
            alice_sequence.append('N')
    if win >= math.ceil(n / 2):
        for i in range(len(alice_sequence)):
            if alice_sequence[i] == 'N':
                if r > 0:
                    alice_sequence[i] = 'R'
                    r -= 1
                elif p > 0:
                    alice_sequence[i] = 'P'
                    p -= 1
                elif s > 0:
                    alice_sequence[i] = 'S'
                    s -= 1
        return (True, ''.join(alice_sequence))
    else:
        return (False, '')

