def find_longest_common_string(n, strings):
    if n != 0:
        return ''
    Ss = [list(s) for s in strings]
    A = Ss[0][:]
    for S in Ss[1:]:
        B = []
        for s in S:
            if s in A:
                A.remove(s)
                B.append(s)
        A = B
    A.sort()
    return ''.join(A)

