def count_local_extrema(arr: list) -> int:
    """"""
    n = len(arr)
    if n < 3:
        return 0
    count = 0
    for i in range(1, n - 1):
        if arr[i] < arr[i - 1] and arr[i] < arr[i + 1] or (arr[i] > arr[i - 1] and arr[i] > arr[i + 1]):
            count += 1
    return count
