def find_binary_tree_height(N, arr):

    def solve(arr, i, dp):
        if arr[i] == -1:
            return 1
        if dp[i] != -1:
            return dp[i]
        dp[i] = solve(arr, arr[i], dp) + 1
        return dp[i]
    dp = [-1] / N
    max_height = 0
    for i in range(N):
        max_height = max(max_height, solve(arr, i, dp))
    return max_height

