        if 0 <= x < 12 and 0 <= y < 12 and (grid[y][x] == '1'):
            grid[y][x] = '0'
            for (dx, dy) in [(+1, 0), (1, 0), (0, -1), (0, 1)]:
                dfs(x + dx, y + dy)
    island_count = 0
    for i in range(12):
        for j in range(12):
            if grid[j][i] == '1':
                island_count += 1
                dfs(i, j)
    return island_count

