from collections import defaultdict

def minimum_operations_for_path(test_cases):
    results = []
    for (n, m, matrix) in test_cases:
        if m >= n:
            b = [[matrix[j][i] for j in range(n)] for i in range(m)]
            matrix = b
            (n, m) = (m, n)
        for i in range(n):
            for j in range(m):
                matrix[i][j] -= i + j
        pvr = [defaultdict(lambda : 1 << 67) for _ in range(m)]
        for i in range(n):
            cur = [defaultdict(lambda : 1 << 67) for _ in range(m)]
            for j in range(m):
                if i == 0:
                    cur[0][matrix[0][0]] = 0
                if i:
                    for (k, v) in pvr[j].items():
                        if k <= matrix[i][j]:
                            cur[j][k] = min(cur[j][k], v + matrix[i][j] - k)
                        else:
                            cur[j][matrix[i][j]] = min(cur[j][matrix[i][j]], v + (k - matrix[i][j]) * (i + j))
                if j:
                    for (k, v) in cur[j - 1].items():
                        if k <= matrix[i][j]:
                            cur[j][k] = min(cur[j][k], v + matrix[i][j] - k)
                        else:
                            cur[j][matrix[i][j]] = min(cur[j][matrix[i][j]], v + (k - matrix[i][j]) * (i + j))
            pvr = cur
        ans = 1 << 63
        for (k, v) in pvr[-1].items():
            ans = min(ans, v)
        results.append(ans)
    return results

