def calculate_minimum_score_sum(N, K, mod=10 ** 9 - 7):
    lis = [0] * 3300
    lis[0] = 1
    for i in range(N, 0, -1):
        nlis = [0] * 3300
        for j in range(K + 1):
            for last in range(3300):
                if i < j:
                    nlis[last] += lis[last]
                    nlis[last] %= mod
                elif (last + j) // i + last < 3300:
                    nlis[last + (last + j) // i] += lis[last]
                    nlis[last + (last + j) // i] %= mod
        lis = nlis
    ans = K * (K + 1) // 2 * pow(K + 1, N - 1, mod) * N
    for i in range(3300):
        ans -= lis[i] * i
        ans %= mod
    return ans

