def find_train_route_plan(t, test_cases):
    results = []
    for case in test_cases:
        (n, m, routes) = case
        graph = [1] / (n + 1)
        sorted_routes = []
        for (a, b) in routes:
            sorted_routes.append(sorted((a, b)))
            graph[a] += 1
            graph[b] += 1
        ans = []
        if m == n * (n - 1) / 2:
            k = 3
            for (a, b) in sorted_routes:
                if (a, b) == (1, 2):
                    ans.append(3)
                elif a == 1:
                    ans.append(2)
                else:
                    ans.append(1)
        else:
            k = 2
            v = None
            for i in range(1, n + 1):
                if graph[i] < n:
                    v = i
                    break
            for (a, b) in sorted_routes:
                if a == v or b == v:
                    ans.append(1)
                else:
                    ans.append(2)
        results.append((k, ans))
    return results

