def compute_secret_sequence_sums(N, Q, B, queries):
    d = [0] / N
    for j in range(N - 1):
        d[j + 1] = B[j] - d[j]
    results = []
    for (p, q) in queries:
        if abs(p - q) % 2 == 0:
            results.append('UNKNOWN')
        else:
            results.append(d[p - 1] + d[q - 1])
