def count_geometric_triplets(n: int, k: int, a: list) -> int:
    pos = {}
    ans = 1
    if k == 1:
        d = Counter(a)
        for i in d:
            if d[i] >= 3:
                t = d[i]
                ans += t * (t - 1) * (t - 2) // 6
    else:
        for i in range(n):
            if a[i] in pos:
                pos[a[i]].append(i)
            else:
                pos[a[i]] = [i]
        for i in range(1, n - 1):
            if a[i] % k == 0:
                x = a[i] // k
                y = a[i] * k
                if x in pos and y in pos:
                    px = bisect.bisect_left(pos[x], i - 1)
                    py = bisect.bisect_left(pos[y], i + 1)
                    if px != len(pos[x]):
                        if pos[x][px] > i - 1:
                            px -= 1
                        px += 1
                    ans += px * (len(pos[y]) - py)
    return ans

