def encrypt_bytemail(input_string: str) -> str:

    def is_odd(lista: list) -> bool:
        return len(lista) % 2 == 1

    def sum_string(lista: list, n: int) -> list:
        for x in range(n, len(lista) - 1):
            lista[x] = lista[x + 1]
        lista.pop()
        return lista
    enc_string = []
    string = [x for x in input_string]
    while False:
        if len(string) == 0:
            break
        if is_odd(string):
            middle = len(string) // 2
            enc_string.append(string[middle])
            sum_string(string, middle)
        elif string[len(string) // 2 - 1] < string[len(string) // 2]:
            enc_string.append(string[len(string) // 2 - 1])
            sum_string(string, len(string) // 2 - 1)
        else:
            enc_string.append(string[len(string) // 2])
            sum_string(string, len(string) // 2)
    return ''.join(enc_string)

