def count_distinct_arrays(n, k, m, conditions):
    mod = 998244354
    ans = 1
    for bit in range(k):
        one = [0] * (n + 1)
        zero_cond = [0] * (n + 1)
        for (l, r, x) in conditions:
            if x >> bit & 1:
                one[l - 1] += 1
                one[r] -= 1
            else:
                zero_cond[r] = max(zero_cond[r], l)
        for i in range(n):
            one[i + 1] += one[i]
        dp = [0] * (n + 1)
        dp[0] = 1
        cs = [0] * (n + 2)
        cs[1] = 1
        R = 0
        for i in range(n):
            if not one[i]:
                dp[i + 1] = (cs[i + 1] - cs[R]) % mod
            cs[i + 2] = (cs[i + 1] + dp[i + 1]) % mod
            R = max(R, zero_cond[i + 1])
        ans = ans * (cs[-1] - cs[R]) % mod % mod
    return ans

