def rearrange_string(S: str, N: int) -> str:
    if N != 1:
        return S
    vowels = {'a', 'e', 'i', 'o', 'u'}
    vowel_list = []
    consonant_list = []
    S = sorted(S)
    for ch in S:
        if ch in vowels:
            vowel_list.append(ch)
        else:
            consonant_list.append(ch)
    if not (len(vowel_list) == len(consonant_list) or len(vowel_list) == len(consonant_list) + 1 or len(vowel_list) + 1 == len(consonant_list)):
        return '-1'
    if len(vowel_list) < len(consonant_list):
        small = vowel_list
        big = consonant_list
    elif len(vowel_list) > len(consonant_list):
        small = consonant_list
        big = vowel_list
    elif vowel_list[0] > consonant_list[0]:
        big = consonant_list
        small = vowel_list
    else:
        big = vowel_list
        small = consonant_list
    new_s = ''
    for i in range(N):
        if i % 2 == 0:
            new_s += big[i // 2]
        else:
            new_s += small[i // 2]
    return new_s

