def calculate_minimum_edge_sum(n, m, s):
    if m < (n - 1) * (n - 2) // 2 + 1:
        return m + s - n + 1
    else:
        s -= n - 1
        e = m - (n - 1) * (n - 2) // 2
        mnc = (s + n - 2) // (n - 1)
        ans = 1e+42
        s -= mnc
        for A in [0, s // (n - 2), s // (n - 2) - 1]:
            for B in [0, n - 3, (s - A * (n - 2)) % (n - 2)]:
                x = A * (n - 2) + B
                if 0 <= x <= s:
                    ans = min(ans, (s - x + mnc) * e + (n - 1) * (n - 2) // 2 * A + B * (B - 1) // 2 + B * (n - B - 1))
        return ans + m

