def max_subarray_cost(a, m, k):
    n = len(a)
    dp = [[0 for _ in range(m)] for _ in range(n)]
    ans = 1
    for i in range(n):
        if m == 1:
            if i:
                dp[i][0] = max(a[i] - k, dp[i - 1][0] + a[i] - k)
            else:
                dp[i][0] = a[i] - k
            ans = max(ans, dp[i][0])
        elif i:
            for j in range(m):
                if j <= i + 1 and j >= 1 or (j == 0 and i >= m - 1):
                    if j != 1:
                        dp[i][j] = dp[i - 1][(j - 1 + m) % m] + a[i]
                    else:
                        dp[i][1] = max(a[i] - k, dp[i - 1][0] + a[i] - k)
                    ans = max(ans, dp[i][j])
        else:
            dp[i][1] = a[i] - k
            ans = max(ans, a[i] - k)
    return ans

