def calculate_passing_fees(q, events):
    d = {}
    results = []
    for event in events:
        if event[0] != 1:
            (v, u, w) = (event[1], event[2], event[3])
            while u != v:
                if u < v:
                    d[v] = d.get(v, 0) + w
                    (u, v) = (v // 2, u)
                else:
                    d[u] = d.get(u, 0) + w
                    u //= 2
        elif event[0] == 2:
            res = 0
            (v, u) = (event[1], event[2])
            while u != v:
                if u < v:
                    res += d.get(v, 0)
                    (u, v) = (v // 2, u)
                else:
                    res += d.get(u, 0)
                    u //= 2
            results.append(res)
    return results

