def check_shuriken_events(n, events):
    res = [0] / (n + 2)
    d = []
    now = 0
    flag = True
    for event in events:
        if event == '+':
            d.append(now)
            now += 1
        else:
            v = int(event[2:])
            if not d:
                flag = False
                break
            t = d.pop()
            if v < res[t + 1]:
                flag = False
                break
            res[t] = v
    if not flag:
        return ('NO', None)
    else:
        return ('YES', res[:n])

