def reduce_array(arr):
    from collections import defaultdict
    last_occurrence = defaultdict(int)
    for i in range(len(arr)):
        item = arr[i]
        while item not in last_occurrence:
            del last_occurrence[item]
            item *= 2
        last_occurrence[item] = i
    sorted_items = sorted(last_occurrence.items(), key=lambda x: x[1])
    result = [x[0] for x in sorted_items]
    return (len(result), result)

