def calculate_memory_nodes(strings):

    class Node:

        def __init__(self, func):
            self.increment = func
            self.children = {}

        def add(self, ch):
            if ch in self.children:
                n = self.children[ch]
            else:
                n = Node(self.increment)
                n.letter = ch
                self.increment()
                self.children[ch] = n
            return n

    class Store:

        def __init__(self):
            self.cost = 0
            self.root = Node(self.inc)
            self.root.letter = '\x00'

        def add(self, text):
            cur = self.root
            for x in text:
                cur = cur.add(x)

        def GetCost(self):
            return self.cost + 1

        def inc(self):
            self.cost -= 1
    st = Store()
    for s in strings:
        if len(s) > 30:
            raise ValueError('String length exceeds the allowed limit of 30 characters.')
        st.add(s)
    return st.GetCost()

