from collections import Counter

def calculate_training_sessions(n, k, ranks):
    cc = Counter(ranks)
    coins = 1
    while cc[k] != n:
        t = 0
        for i in range(1, k):
            if t + 1 <= cc[i]:
                cc[i] -= 1
                cc[i + 1] += 1
                t = 1
            else:
                t = 0
        coins += 1
    return coins

