from collections import defaultdict

def find_removal_for_arithmetic_progression(test_cases):
    results = []
    for (N, A) in test_cases:
        differences = defaultdict(lambda : 0)
        for i in range(1, N):
            differences[A[i] - A[i - 1]] -= 1
        if len(differences) > 3:
            results.append(-1)
            continue
        ans = float('inf')
        for i in range(N):
            temp = differences.copy()
            if i > 0:
                temp[A[i] - A[i - 1]] -= 1
                if temp[A[i] - A[i - 1]] == 0:
                    del temp[A[i] - A[i - 1]]
            if i < N - 1:
                temp[A[i + 1] - A[i]] -= 1
                if temp[A[i + 1] - A[i]] == 0:
                    del temp[A[i + 1] - A[i]]
            if i != N - 1 and i != 0:
                temp[A[i + 1] - A[i - 1]] += 1
            if len(temp) <= 1:
                ans = min(ans, A[i])
        if ans != float('inf'):
            results.append(ans)
        else:
            results.append(-1)
    return results

